import React from "react";
import {Row, Col, Container} from "../Grid";
import Button from "../Button";
import "./style.css"
import { Link } from "react-router-dom";

export function HandleSearch(infoLink){
	location.href = infoLink;
}

export function BooksList({children}) {
	return <ul className="list-group">{children}</ul>;
}

export function BooksListItem({
	
	props,
	title,
	subtitle = "",
	authors,
	image,
	description,
	infoLink,
	allowSave,
	allowDelete,
	onSave,
	onDelete,
}) {
		return (
			<Container>
				<div className="border">
					<div className="d-flex justify-content-between">
						<h4 className="m-3">{title}</h4>
							<section>
								
									<Button
										onClick={HandleSearch({infoLink})}
										type="primary"
										text="View"
									/>

									{allowSave && <Button
										onClick={onSave}
										type="success"
										text="Save"
									/>}
									
									{allowDelete && <Button
										onClick={onDelete}
										type="danger"
										text="Delete"
									/>}

							</section>    
					</div>
					<h5 className="m-3">{subtitle}</h5>
					<p className="ml-3">{authors}</p>
					
					<Row>
						<Col size="xs-4 sm-2">
								<img className="test m-3" src={image} />
						</Col>
						<Col size="xs-8 sm-9">
								<p>{description}
								</p>
						</Col>
					</Row>
				</div>
			</Container>
		)
}


